function ClearStuContextFields(oFormControl, oFormBody){

  if (typeof(oFormControl.STUID) != "undefined")
    oFormControl.STUID.value = "";

  if (typeof(oFormBody.StudentList) != "undefined")
    oFormBody.StudentList.options.length = 0;

  if (typeof(oFormBody.DegreeList) != "undefined")
    oFormBody.DegreeList.options.length = 0;

  if (typeof(oFormBody.Major) != "undefined")
    oFormBody.Major.value = "";

  if (typeof(oFormBody.School) != "undefined")
    oFormBody.School.value = "";

  if (typeof(oFormBody.Level) != "undefined")
    oFormBody.Level.value = "";

  if (typeof(oFormBody.Custom) != "undefined")
    oFormBody.Custom.value = "";

  if (typeof(oFormBody.LastAudit) != "undefined")
    oFormBody.LastAudit.value = "";

  if (typeof(oFormBody.LastRefresh) != "undefined")
    oFormBody.LastRefresh.value = "";


  oFormBody.StudentID.focus();
}

////////////////////////////////////////////////////////////////////////////
// REPLACEHTMLCHARS
// Replace "&" with the Ampersand character "&"
// Replace "'" with the single quote character "'"
// Compatibility tested OK with NS 4.79
////////////////////////////////////////////////////////////////////////////
function ReplaceHTMLChars(sString)
{
        sString = sString.replace(/&/g, "&");
        sString = sString.replace(/'/g, "'");
        return sString;
} // replacehtmlchars

//////////////////////////////////////////////////
function DataItem(sCode, sLiteral) {
        this.code = sCode;
        this.literal = sLiteral;
}
//////////////////////////////////////////////////
function GoalDataItem (sSchool, sDegree, sLevel, sMajor, sDegreeSource) {
        this.school = sSchool;
        this.degree = sDegree;
        this.level  = sLevel;
        this.major  = sMajor;
		this.degreesource = sDegreeSource;
        this.schoolLit = "";
        this.degreeLit = "";
        this.levelLit  = "";
        this.majorLit  = "";
}

//////////////////////////////////////////////////
// PARSE CODES
//////////////////////////////////////////////////
function ParseCodes ()
{

// Get the corresponding literal for each code
for (iGoalIndex = 0; iGoalIndex < aGoalArray.length; iGoalIndex++)
{
  // Set defaults in case codes not found in Picklist arrays
  aGoalArray[iGoalIndex].degreeLit = aGoalArray[iGoalIndex].degree;
  aGoalArray[iGoalIndex].majorLit  = aGoalArray[iGoalIndex].major;
  aGoalArray[iGoalIndex].levelLit  = aGoalArray[iGoalIndex].level;
  //Degree
  for (iPickIndex = 0; iPickIndex < sDegreePicklist.length; iPickIndex++)
  {
	if (Trim(sDegreePicklist[iPickIndex].code) == Trim(aGoalArray[iGoalIndex].degree))
    {
	  aGoalArray[iGoalIndex].degreeLit = ReplaceHTMLChars(sDegreePicklist[iPickIndex].literal);
      // If this is an applicant goal add an asterisk to the end of the degree literal
	  if (aGoalArray[iGoalIndex].degreesource == "A")
	  {
	    aGoalArray[iGoalIndex].degreeLit = Trim(aGoalArray[iGoalIndex].degreeLit) + "*";
	  }
	  break;
    }
  }
  //Major
  for (iPickIndex = 0; iPickIndex < sMajorPicklist.length; iPickIndex++)
  {
	if (Trim(sMajorPicklist[iPickIndex].code) == Trim(aGoalArray[iGoalIndex].major))
    {
	  aGoalArray[iGoalIndex].majorLit =	ReplaceHTMLChars(sMajorPicklist[iPickIndex].literal);
      break;
    }
  }
  //Level
  for (iPickIndex = 0; iPickIndex < sLevelPicklist.length; iPickIndex++)
  {
	if (Trim(sLevelPicklist[iPickIndex].code) == Trim(aGoalArray[iGoalIndex].level))
    {
	  aGoalArray[iGoalIndex].levelLit =	ReplaceHTMLChars(sLevelPicklist[iPickIndex].literal);
 break;
    }
  }
} // for codeindex

} // parsecodes


