ClearStuContextFields(top.frControl.document.frmCallScript,
                      top.frControl.document.frmHoldFields);
sStuID = "72061444";
with (top.frControl)
{
  InitializeArrays();
  if (sILStudentName.length == 0 || aGoalArray.length == 0) // No name or goal data found
  {
    if (sILStudentName.length == 0)
      alert ("Student information for ID " + sStuID + " not found");
    else if (aGoalArray.length == 0)
      alert ("Student " + sILStudentName + " found for ID " + sStuID + " but degree information not found");
    parent.frBody.location.href = "SD_GeneralIntroduction.html"; // 1.1f
    parent.frSelection.location.href = "SD_LoadFrameForm.html"; // 1.1f
  }
  else // Student information found
  {
    AddStudent(sStuID, sILStudentName);        //1.1e end
    AddAuditDate(sStuID, '20170730');
    if (bRefreshUsed)
    {
        AddRefreshDateTime (sStuID, '20170626',
        '1049');
    }
    // Add each degree for this student
    for (iStuIndex = 0; iStuIndex < aGoalArray.length; iStuIndex++)
    {
      sDegreeInterest = "  "; // No more deg-ints; use a dummy value
      AddDegree(sStuID,
                aGoalArray[iStuIndex].degree,    // Degree Key
                aGoalArray[iStuIndex].degreeLit, // Degree Short Literal
                aGoalArray[iStuIndex].school,    // School
                aGoalArray[iStuIndex].majorLit,  // Major Literal
                aGoalArray[iStuIndex].levelLit,  // Level Literal
                sDegreeInterest);                // Degree Interest
    } // for
    SelectIDStudents();
    PopulateStudentList();

    // // When student is loaded click the Audit/Worksheets tab
    // top.frTabs.SwapDown('Tab_Audit');
    // top.frTabs.SwapOn('Tab_Audit');
    // top.frTabs.sHelpPage = 'SD_HelpAuditView.html';
    // top.frControl.CallScript('SD2AUDCON');
    // top.frTabs.SwapOut('Tab_Audit');
  }
}
