var wAddNote = null;

///////////////////////////////////////////////////////
function AddNote ()
{
// If the window exists and has not been closed
if (wAddNote != null && !wAddNote.closed)
  {
  // The windows was previously created and placed in the background
  wAddNote.focus();
  return;
  }

// Create a new window for the add-note to appear
var iWidth  = 700;
var iHeight = 450; // needs to be bigger for Firefox
var iXPlace = 300;
var iYPlace = 200;
var sWindowParams = "width=" + iWidth + ",height=" + iHeight + "," +
            "toolbar=no,location=no,directories=no,status=no," +
            "menubar=no," + "scrollbars=yes,resizable=yes";
var sWindowName = "wAddNote";
wAddNote = window.open("", sWindowName, sWindowParams);
wAddNote.resizeTo (iWidth, iHeight);
//wAddNote.moveTo   (iXPlace, iYPlace);

// Allow the pop-up to reference the main window
wAddNote.opener = self;
// Get the add-note page and place it in the new window
top.frControl.GetScript('SD2NTEADD', 'no-action&ADDNOTEPOPUP=Y', 'no-report', sWindowName);
// In case it was placed in the background previously
wAddNote.focus();

} // addnote

