function RefreshStudent ()
{
    //alert("this will do something someday");
    if (top.frControl.studentArray.length != 0)
    {
        if (confirm("This student's data was last refreshed " +
               document.frmHoldFields.LastRefresh.value + " \n" +
               "Do you want to refresh again now?"))
        {
            refreshForm = document.frmRefresh;
            refreshForm.STUID.value = top.frControl.iCurrentID;
            top.frBody.location.href = "SD_Waiting.html";
            refreshForm.submit();
        }
    }
    else
    {
        alert("Please select a student before proceeding.");
    }
}
