function ToggleRELOADSEP(sTrueOrFalse) // 1.05 New
{
    if (sTrueOrFalse == "FALSE")
        document.frmCallScript.RELOADSEP.value = "FALSE";
    else
        document.frmCallScript.RELOADSEP.value = "TRUE";
}
// Reload the tab the user is on but using the new degree selected
// or when a new person is selected from the Name picklist
function  ReloadTab()
{
  if (sTabContext == "PLANNER")
    top.frTabs.doPlanner();
  else if (sTabContext == "NOTES")
    top.frTabs.doNotes();
  else if (sTabContext == "PETITIONS")
    top.frTabs.doPetitions();
  else if (sTabContext == "EXCEPTIONS")
    top.frTabs.doExceptions();
  else if (sTabContext == "GPACALC")
    top.frTabs.doGPACalc();
  else if (sTabContext == "ADMIN")
    top.frTabs.doAdmin();
  else // do worksheet tab
    top.frTabs.doAudit();
}
var iSleepCount = 0;
var iSleepMax = 100; // 100 x 100ms = 10 seconds
var iSleepMilliseconds = 100; // for 1/10th of a second
/////////////////////////////////////////////////////////////
function waitForFrameset()
{
  iSleepCount = 0;
  checkFramesetLoaded();
}
/////////////////////////////////////////////////////////////
function checkFramesetLoaded()
{
  // If the frames finally loaded or we slept long enough; see Functions.js
  if (top.bBodyFramesetLoaded || iSleepCount > iSleepMax)
  {
    //alert ("SD2STUCON: checkFramesetLoaded: top.bBodyFramesetLoaded = " + top.bBodyFramesetLoaded + "; iSleepCount = " + iSleepCount);
    reallyLoadWorksheetTab();
    return;
  }

  iSleepCount++;
  setTimeout ("checkFramesetLoaded()", iSleepMilliseconds);
}
/////////////////////////////////////////////////////////////
function  LoadWorksheetTab()
{
   if (top.isZkLoaded())
   {
      top.reloadBodyFrameset(); // sets top.bBodyFramesetLoaded; see Functions.js
      waitForFrameset();
   }
   else
   {
      reallyLoadWorksheetTab();
   }
}
/////////////////////////////////////////////////////////////
function reallyLoadWorksheetTab()
{
    // When student is loaded click the Audit/Worksheets tab
    top.frTabs.sHelpPage = 'SD_HelpAuditView.html';
    top.frTabs.tabActivateFirst();
    CallScript('SD2AUDVERT', "", "frLeft");
}

