function SetElementTitles()
{
	sForm = document.frmHoldFields;

	myStudentId = Trim("Student ID          ");
	myDegree    = Trim("Degree              ");
	myMajor     = Trim("Major               ");
	mySchool    = Trim("Level               ");
	myLevel     = Trim("Classification      ");

	sForm.StudentID.title = myStudentId;
	document.getElementById("StudentIdLabel").title = myStudentId;
	document.getElementById("DegreeLabel").title = myDegree;

	sForm.Major.title = myMajor;
	document.getElementById("MajorLabel").title = myMajor;


	sForm.Level.title = myLevel;
	document.getElementById("LevelLabel").title = myLevel;

}

////////////////////////////////////////////////////////////////////////////
// REPLACEHTMLCHARS
// Replace "&" with the Ampersand character "&"
// Replace "'" with the single quote character "'"
// Compatibility tested OK with NS 4.79
////////////////////////////////////////////////////////////////////////////
function ReplaceHTMLChars(sString)
{
        sString = sString.replace(/&/g, "&");
        sString = sString.replace(/'/g, "'");
        return sString;
} // replacehtmlchars


