function DoInitialize()
{
var bGetMyStudents  = false;
var bGetMyOwnDegree = false;
var bGetPortalStuId = false;
var sPortalStudentId = "";

InitializeSettings();

  GetOwnDegreeSubmit();

sPortalStudentId = "";
if (Trim(sPortalStudentId).length > 0)
 bGetPortalStuId = true;

if (bGetMyOwnDegree) // if student make sure she can only see herself
  GetOwnDegreeSubmit();
else if (bGetPortalStuId) // if id passed from portal use it
  GetPortalStudentSubmit(sPortalStudentId);
else if (bGetMyStudents)  // advisor/dept - get list of students automatically
  GetMyStudentsSubmit();
else // reg user etc - allow user to perform a search - no automatic loading
  {
  // do nothing - let user search
  }

SetElementTitles();
} // doinitialize

function GetCourseInfoSEPView (sBuffer, sTerm, sReport, sUserClass)
{
// Get form we will submit
submitForm = top.frames.frControl.document.forms.frmCourseInfoSEPView;

if (sBuffer == "NOCLASSESFOUND")
  {
  alert ("No classes found to get course information");
  }
else // classes found
  {
  var sWindowParams = "width=600,height=600" + ",toolbar=no,location=no,directories=no,status=no,menubar=no,scrollbars=yes,resizable=yes";
  var sWindowName = "wCourseInfo";
  var wNew = window.open("", sWindowName, sWindowParams);
  //wNew.resizeTo (iWidth, iHeight);
  //wNew.moveTo   (iXPlace, iYPlace);
  // The control frame has the form to submit

  submitForm.target = sWindowName;
  submitForm.BUFFER.value = "&";
  submitForm.BUFFER.value += parseCourseKeyString(sBuffer);
  submitForm.BUFFER.value += "TERM=" + sTerm + "&";
  submitForm.BUFFER.value += "REPORT=" + sReport + "&";
  submitForm.BUFFER.value += "REPORTUCLASS=" + sUserClass + "&";

  //alert("submitForm.BUFFER.value  = " + submitForm.BUFFER.value );
  submitForm.submit();
  wNew.focus(); // needed in case window was already open
  }

} // GetCourseInfoSEPView

function parseCourseKeyString(sString)
{
	// | is the separator between disc and number, like this: MATH|101
	// ^ is the separator between course keys, like this: MATH|101^MATH103
	var sReturn = "";
	var sThisString = sString;
	var sThisCourse = "";

	CourseKeyCheck = sString.indexOf("^");


	if (CourseKeyCheck > 0)
	{
		while (sThisString.length > 1)
		{
			iThisLength = sThisString.length;
			DivisorPos = sThisString.indexOf("^");
			sThisCourse = sThisString.substring(0, DivisorPos);
			KeyDivisorPos = sThisCourse.indexOf("|");
			sReturn += 'COURSEDISC="'
			sReturn += sThisCourse.substring(0, KeyDivisorPos);
			sReturn += '"&'
			sReturn += 'COURSENUMB="'
			sReturn += sThisCourse.substring(KeyDivisorPos+1, sThisCourse.length);
			sReturn += '"&'
			sReturn += 'COURSEATTR=&' // always blank
			sThisString = sThisString.substring(DivisorPos+1, iThisLength);
			//alert("sReturn = " + sReturn);
		}
	return sReturn;
	}
}

